mapper.select:
SELECT * FROM ( SELECT U.*, ROWNUM RN FROM ( SELECT LODGMENT_NO, COMPANY, ADDRESS, LOCATION, LODGMENT_TYPE, LODGMENT_INFO, HOST_NAME, LODGMENT_IMG, case LODGMENT_TYPE when 'A1' then '모텔' when 'A2' then '호텔' when 'A3' then '펜션' when 'A4' then '게스트하우스' end LODGMENT_TYPE_NAME, (SELECT COUNT(*) FROM ROOM WHERE ROOM.LODGMENT_NO = LODGMENT.LODGMENT_NO) AS roomCnt FROM LODGMENT
LODGMENT_NO IN
#{item}
AND LOCATION = #{location}
AND LODGMENT_NO LIKE '%' || #{searchKeyword} || '%'
AND COMPANY LIKE '%' || #{searchKeyword} || '%'
AND LOCATION LIKE '%' || #{searchKeyword} || '%'
ORDER BY SIGNUP_DATE DESC
) U ) A WHERE RN BETWEEN #{start} and #{end}

SELECT COUNT(*) FROM LODGMENT
LODGMENT_NO IN
#{item}
AND LOCATION = #{location}
AND COMPANY LIKE '%' || #{searchKeyword} || '%'
AND LOCATION LIKE '%' || #{searchKeyword} || '%'

SELECT l.LODGMENT_IMG ,l.LODGMENT_NO,l.COMPANY, g.AVG_GRADE, g.people_cnt FROM LODGMENT l LEFT OUTER JOIN (SELECT ROUND(AVG(GRADE),1) AS AVG_GRADE ,COUNT(*) AS people_cnt, LODGMENT_NO FROM POSTSCRIPT GROUP BY LODGMENT_NO) g ON l.LODGMENT_NO = g.LODGMENT_NO
AND l.LOCATION = #{location}
AND l.LODGMENT_TYPE = #{lodgmentType}
AND l.LODGMENT_NO IN (SELECT LODGMENT_NO FROM HASHTAG WHERE HASHTAG_CONTENT = #{searchKeyword})
ORDER BY #{sort} DESC

mapper.update:
UPDATE LODGMENT


	  	COMPANY 	 	= #{company},


	 	 LODGMENT_TYPE 	= #{lodgmentType},


	  	  LODGMENT_IMG	= #{lodgmentImg},


	  		LODGMENT_INFO 	= #{lodgmentInfo}



WHERE LODGMENT_NO	= #{lodgmentNo}
