mapper.select:
SELECT * FROM ( SELECT U.*, ROWNUM RN FROM ( SELECT RESERVE_NO, (SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = RESERVATION.MEMBER_NO) AS memberName, (SELECT MEMBER_EMAIL FROM MEMBER WHERE MEMBER.MEMBER_NO = RESERVATION.MEMBER_NO) AS memberEmail, ROOM_NO, RESERVE_DATE, RESERVE_PRICE, RESERVE_PEOPLE, CHECKIN, CHECKOUT, RESERVE_STATE, case RESERVE_STATE when 'D1' then '확인' else '미확인' end RESERVE_STATE_NAME FROM RESERVATION
RESERVE_NO IN
#{item}
AND #{searchKeyword} IN (SELECT MEMBER_NAME FROM MEMBER WHERE RESERVATION.MEMBER_NO = MEMBER.MEMBER_NO)
AND #{searchKeyword} IN (SELECT MEMBER_EMAIL FROM MEMBER WHERE RESERVATION.MEMBER_NO = MEMBER.MEMBER_NO)
AND RESERVE_NO LIKE '%' || #{searchKeyword} || '%'
AND ROOM_NO LIKE '%' || #{searchKeyword} || '%'
ORDER BY RESERVE_DATE DESC
) U ) A WHERE RN BETWEEN #{start} and #{end}
