mapper.insert:
 INSERT INTO POSTSCRIPT(
POSTSCRIPT_NO
 , POSTSCRIPT_CONTENT
 , POSTSCRIPT_DATE
 , LODGMENT_NO
 , POSTSCRIPT_TITLE
 , ROOM_NO
 , MEMBER_NO
 , GRADE
 )
 VALUES(
 lpad(POSTSCRIPT_SEQ.NEXTVAL ,8,0)
 , #{postscriptContent}
 , SYSDATE
 , #{lodgmentNo}
 , #{postscriptTitle}
 , #{roomNo}
 , #{memberNo}
 , #{grade}
 )
