mapper.select:
SELECT * FROM ( SELECT U.*, ROWNUM RN FROM ( SELECT TOUR_ID ,TOUR_TITLE ,(SELECT MEMBER_NO FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberNo ,(SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberName ,TO_CHAR(TOUR_DATE,'YYYY-MM-DD') AS TOUR_DATE ,TOUR_LOCATION ,TOUR_STATE FROM TOUR
TOUR_STATE = #{tourState}
AND TOUR_ID LIKE '%' || #{searchKeyword} || '%'
AND TOUR_TITLE LIKE '%' || #{searchKeyword} || '%'
AND #{searchKeyword} IN (SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO)
AND TOUR_LOCATION LIKE '%' || #{searchKeyword} || '%'
ORDER BY
#{sort}
TOUR_DATE DESC
) U ) A WHERE RN BETWEEN #{start} and #{end}

mapper.update:
UPDATE TOUR
SET TOUR_STATE = 'B1'			
WHERE TOUR_ID = #{tourId}

UPDATE TOUR
SET TOUR_STATE = 'B2'			
WHERE TOUR_ID = #{tourId}

mapper.select:
SELECT * FROM ( SELECT T.*, ROWNUM RN FROM (   
   SELECT * FROM TOUR



            TOUR_ID > #{tourId}



    ORDER BY TOUR_ID
) T ) A 



         WHERE RN  <= #{pageUnit}




         WHERE RN <= 8
